mapper.select:
SELECT * FROM shopping_cart_items
WHERE cart_id = #{cartId}
ORDER BY create_time ASC

SELECT * FROM shopping_cart_items
WHERE cart_id = #{cartId} AND product_id = #{productId}

    AND sku_id = #{skuId}


    AND sku_id IS NULL

LIMIT 1

mapper.delete:
DELETE FROM shopping_cart_items
WHERE cart_id = #{cartId} AND product_id = #{productId}

    AND sku_id = #{skuId}


    AND sku_id IS NULL
