mapper.insert:
INSERT INTO shopping_carts (
    user_id, table_id, merchant_id, store_id, shard_id,
    total_amount, total_quantity, expire_time, version,
    create_time, update_time
) VALUES (
    #{userId}, #{tableId}, #{merchantId}, #{storeId}, #{shardId},
    #{totalAmount}, #{totalQuantity}, #{expireTime}, #{version},
    #{createTime}, #{updateTime}
)

mapper.select:
SELECT * FROM shopping_carts
WHERE user_id = #{userId} AND store_id = #{storeId}

    AND table_id = #{tableId}


    AND table_id IS NULL

LIMIT 1

SELECT * FROM shopping_carts
WHERE table_id = #{tableId} AND store_id = #{storeId}
LIMIT 1

SELECT * FROM shopping_carts
WHERE user_id = #{userId}
ORDER BY create_time DESC
