mapper.select:
SELECT * FROM poi_item_channel_stock
WHERE stock_id = #{stockId}
  AND channel_code = #{channelCode}
LIMIT 1

SELECT * FROM poi_item_channel_stock
WHERE stock_id = #{stockId}
ORDER BY channel_code

mapper.update:
UPDATE poi_item_channel_stock
SET channel_sold = channel_sold + #{delta},
    utilization_rate = ROUND((channel_sold + #{delta}) / NULLIF(channel_quota, 0) * 100, 1),
    updated_at = NOW()
WHERE stock_id = #{stockId}
  AND channel_code = #{channelCode}
  AND stock_status = 1
  AND (channel_quota - channel_sold) >= #{delta}

UPDATE poi_item_channel_stock
SET borrowed_from_pool = borrowed_from_pool + #{amount},
    updated_at = NOW()
WHERE stock_id = #{stockId}
  AND channel_code = #{channelCode}

UPDATE poi_item_channel_stock
SET contributed_to_pool = contributed_to_pool + #{amount},
    updated_at = NOW()
WHERE stock_id = #{stockId}
  AND channel_code = #{channelCode}

mapper.select:
SELECT * FROM poi_item_channel_stock
WHERE stock_id = #{stockId}
  AND stock_status = 1
  AND channel_quota > 0
  AND (channel_sold / channel_quota * 100) < #{lowWatermark}
ORDER BY utilization_rate ASC

SELECT * FROM poi_item_channel_stock
WHERE stock_id = #{stockId}
  AND stock_status = 1
  AND channel_quota > 0
  AND (channel_sold / channel_quota * 100) > #{highWatermark}
ORDER BY utilization_rate DESC
