mapper.select:
SELECT * FROM poi_item_channel_stock
WHERE id = #{id}
LIMIT 1

SELECT * FROM poi_item_channel_stock
WHERE stock_id = #{stockId}
  AND channel_code = #{channelCode}
LIMIT 1

SELECT * FROM poi_item_channel_stock
WHERE stock_id = #{stockId}
ORDER BY channel_code

mapper.update:
UPDATE poi_item_channel_stock
SET channel_sold = channel_sold + #{delta},
    updated_at = NOW()
WHERE stock_id = #{stockId}
  AND channel_code = #{channelCode}
  AND (COALESCE(channel_max, 0) = 0 OR (channel_sold + #{delta}) <= channel_max)

UPDATE poi_item_channel_stock
SET channel_sold = channel_sold + #{delta},
    updated_at = NOW()
WHERE stock_id = #{stockId}
  AND channel_code = #{channelCode}
  AND stock_status = 1
  AND (channel_quota - channel_sold) >= #{delta}

UPDATE poi_item_channel_stock
SET channel_sold = channel_sold - #{qty},
    updated_at = NOW()
WHERE stock_id = #{stockId}
  AND channel_code = #{channelCode}
  AND channel_sold >= #{qty}

mapper.select:
SELECT COALESCE(SUM(safety_stock), 0)
FROM poi_item_channel_stock
WHERE stock_id = #{stockId}
  AND channel_priority > #{myPriority}
  AND safety_stock > 0
