mapper.insert:
INSERT INTO menu_items (
    merchant_id, product_shard_id, item_id, img_info, version, create_time, update_time
) VALUES (
    #{merchantId}, #{productShardId}, #{itemId}, #{imgInfo}, 1, NOW(), NOW()
)

    SELECT version FROM menu_items WHERE merchant_id = #{merchantId} AND item_id = #{itemId} ORDER BY id DESC LIMIT 1

mapper.select:
SELECT

FROM menu_items
WHERE merchant_id = #{merchantId} AND item_id = #{itemId}

mapper.update:
UPDATE menu_items SET
    img_info = #{imgInfo},
    version = version + 1,
    update_time = NOW()
WHERE merchant_id = #{merchantId} AND item_id = #{itemId} AND version = #{version}

    SELECT version FROM menu_items WHERE merchant_id = #{merchantId} AND item_id = #{itemId}
