mapper.insert:
INSERT INTO product_sku (
    product_id, store_id, sku_code, sku_attributes, sku_name, sku_price, sku_image, status, is_delete, version
) VALUES (
    #{productId}, #{storeId}, #{skuCode}, #{skuAttributes}, #{skuName}, #{skuPrice}, #{skuImage}, 
    #{status}, #{isDelete}, #{version}
)


    SELECT id FROM product_sku WHERE product_id = #{productId} AND store_id = #{storeId} AND sku_code = #{skuCode} AND is_delete = 0 ORDER BY id DESC LIMIT 1

mapper.update:
UPDATE product_sku SET
    is_delete = 1,
    version = version + 1,
    update_time = NOW()
WHERE id = #{id} AND is_delete = 0 AND version = #{version}

    SELECT version FROM product_sku WHERE id = #{id}
