mapper.insert:
INSERT INTO product_sku (
    product_id, store_id, sku_code, sku_attributes, sku_name, sku_price, sku_image, status, is_delete, version
) VALUES (
    #{productId}, #{storeId}, #{skuCode}, #{skuAttributes}, #{skuName}, #{skuPrice}, #{skuImage}, 
    #{status}, #{isDelete}, #{version}
)



    SELECT id FROM product_sku WHERE product_id = #{productId} AND store_id = #{storeId} AND sku_code = #{skuCode} ORDER BY id DESC LIMIT 1

mapper.select:
SELECT id, store_id, product_id, sku_code, sku_attributes, sku_price, sku_image, status, sku_name, version, create_time, update_time, is_delete
FROM product_sku 
WHERE id = #{id}

mapper.update:
UPDATE product_sku SET
    is_delete = 1,
    version = version + 1,
    update_time = NOW()
WHERE id = #{id} AND version = #{version}

    SELECT version FROM product_sku WHERE id = #{id}
