mapper.insert:
INSERT INTO store_products (
    store_id, product_shard_id, product_name, description, unit_price, product_image, category, status, version

        , create_time


        , update_time

) VALUES (
    #{storeId}, #{productShardId}, #{productName}, #{description}, #{unitPrice}, #{productImage}, #{category}, #{status}, 1

        , #{createTime}


        , #{updateTime}

)


    SELECT id FROM store_products 
    WHERE store_id = #{storeId} AND product_name = #{productName} AND is_delete = 0
    ORDER BY id DESC LIMIT 1

mapper.select:
SELECT * FROM store_products 
WHERE store_id = #{storeId} AND product_name = #{productName}
ORDER BY id DESC LIMIT 1

mapper.update:

    SELECT version FROM store_products WHERE id = #{id}

UPDATE store_products 
SET is_delete = 1, version = version + 1 
WHERE id = #{id} AND is_delete = 0 AND version = #{version}

UPDATE store_products 
SET version = version + 1
WHERE id = #{productId}
